import React, { useState } from 'react';

function getQueryParam(param) {
  const params = new URLSearchParams(window.location.search);
  return params.get(param);
}

const RegisterForm = () => {
  const [form, setForm] = useState({
    nombre: '',
    email: '',
    telefono: ''
  });
  const [enviado, setEnviado] = useState(false);

  const id_reserv = getQueryParam('id_reserv');
  const mesa = getQueryParam('mesa');

  // Mostrar en consola para pruebas
  console.log('ID Reserva:', id_reserv, 'Mesa:', mesa);

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    // Aquí puedes enviar los datos al backend para guardarlos
    await fetch('https://covermanager-883996440.development.catalystserverless.com/server/qrbackend/guardar-registro', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        ...form,
        id_reserv,
        mesa
      })
    });
    setEnviado(true);
  };

  if (enviado) return <p>¡Registro enviado correctamente!</p>;

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <strong>ID Reserva:</strong> {id_reserv}
      </div>
      <div>
        <strong>Mesa:</strong> {mesa}
      </div>
      <input
        name="nombre"
        placeholder="Nombre"
        value={form.nombre || ''}
        onChange={handleChange}
        required
      />
      <input
        name="email"
        placeholder="Email"
        type="email"
        value={form.email || ''}
        onChange={handleChange}
        required
      />
      <input
        name="telefono"
        placeholder="Teléfono"
        value={form.telefono || ''}
        onChange={handleChange}
        required
      />
      <button type="submit">Registrar</button>
    </form>
  );
};

export default RegisterForm;
