import React, { useState } from 'react';

function getQueryParam(param) {
    const params = new URLSearchParams(window.location.search);
    return params.get(param);
}

const RegisterForm = () => {
    const [form, setForm] = useState({
        nombre: '',
        email: '',
        telefono: ''
    });
    const [enviado, setEnviado] = useState(false);

    const id_reserv = getQueryParam('id_reserv');
    const mesa = getQueryParam('mesa');

    const handleChange = e => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = async e => {
        e.preventDefault();
        // Aquí puedes enviar los datos al backend para guardarlos
        // Incluye id_reserv y mesa en el body
        await fetch('http://localhost:5000/guardar-registro', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                ...form,
                id_reserv,
                mesa
            })
        });
        setEnviado(true);
    };

    if (enviado) return <p>¡Registro enviado correctamente!</p>;

    return (
        <form onSubmit={handleSubmit}>
            <input
                name="nombre"
                placeholder="Nombre"
                value={form.nombre || ''}
                onChange={handleChange}
                required
            />
            <input
                name="email"
                placeholder="Email"
                type="email"
                value={form.email || ''}
                onChange={handleChange}
                required
            />
            <input
                name="telefono"
                placeholder="Teléfono"
                value={form.telefono || ''}
                onChange={handleChange}
                required
            />
            {/* Campos ocultos */}
            <input type="hidden" name="id_reserv" value={id_reserv} />
            <input type="hidden" name="mesa" value={mesa} />
            <button type="submit">Registrar</button>
        </form>
    );
};

export default RegisterForm;
