// Nuevo componente QRViewer.js
import React, { useState } from 'react';

const QRViewer = () => {
  const [qrUrl, setQrUrl] = useState(null);

  const generarQR = async () => {
    const resp = await fetch('http://localhost:5000/generate-qr', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ id_reserv: 'jAJ8Ks', tables: '1010' })
    });
    const data = await resp.json();
    setQrUrl(data.qrUrl);
  };

  return (
    <div>
      <button onClick={generarQR}>Generar QR</button>
      {qrUrl && <img src={qrUrl} alt="CÃ³digo QR" />}
    </div>
  );
};

export default QRViewer;
